components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                <a class="icon icon--{{display_name}}" href="{{url}}" title="{{name}}" rel="noopener">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    {{name}}
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}

pages/auth/forgot-password:
{{#partial "page"}}
 {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
    <div class="account page-content--centered">
        <h2 class="page-heading"><span>{{lang 'reset_password.heading' }}</span></h2>
        <p class="tt-pass-intro">{{lang 'reset_password.intro' }}</p>
        {{#if forms.forgot_password.error}}
            {{> components/common/alert-error forms.forgot_password.error}}
        {{/if}}
        <form action="{{urls.auth.send_password_email}}" class="form forgot-password-form" method="post">
            {{inject 'useValidEmail' (lang 'forms.validate.common.email_address')}}
            <label class="form-label" for="email">{{lang 'common.email_address'}}</label>
            <div class="form-prefixPostfix wrap">
                <input class="form-input" name="email" id="email" type="email" formnovalidate>
                <input type="submit" class="button button--primary form-prefixPostfix-button--postfix" value="{{lang 'reset_password.heading' }}">
            </div>
        </form>
    </div>
{{/partial}}
{{> layout/base}}
